import axios from 'axios';

class App extends Component {
      constructor(props){
        super(props)
        this.state = {
          todos: []
        }
      }

getData = () => {
  axios.get('https://btm-rn.herokuapp.com.api.v1/todo')
  .then(res => this.setState({ todos: res.data.results }))
}

componentDidMount() {
  this.getData()
  setInterval(this.getData, 100)
}

render() {
  return (
    <div>

